import sysfilename = input('Which data file do you want to use? ')try:        file = open(filename)    quads = []    for line in file:        try:            x = line.lstrip("R")            y = x.rstrip("\n")            quads.append(eval(y))        except:            continueexcept:    print("Error: the file name you entered is not valid.")    sys.exit()for i in range(len(quads)):    for j in range(len(quads)):        for k in range(len(quads)):            if (i,j) in quads and (j,k) in quads:                a = (i,k)            try:                quads.remove(a)            except:                continuei=0root_nodes =[]for i in range(len(quads)):    root_nodes.append(quads[i][0])    i+=1end_nodes = []i=0for i in range(len(quads)):    end_nodes.append(quads[i][1])    i+=1a = []i=0for i in root_nodes:    if i not in end_nodes:        try:            a.append(i)        except:            continuea = list(set(a))b = []i=0for i in end_nodes:    if i not in root_nodes:        try:            b.append(i)        except:            continueb = list(set(b))        d= []for i in range(len(a)):    for j in range(len(b)):        c =(a[i],b[j])        d.append(c)for i in d:    if i in quads:        try:            quads.remove(i)        except:            continue        for i in range(len(quads)):    print("R{}".format(quads[i]))    '''y = []i=0j=0for i in range(len(quads)):    for j in range(len(quads)):        if quads[i][1] == quads[j][0]:            try:                y.append((quads[i][0], quads[j][1]))#                x.append(quads[j][1]) #       j+=1#    i+=1            except:                continueprint(y)result = [x for x in quads if x not in y]print(result)'''